	/**
	 * Sets contents of passed object to values from current object.
	 *
	 * If desired, this method can also make copies of all associated (fkey referrers)
	 * objects.
	 *
	 * @param      object $copyObj An object of Seat (or compatible) type.
	 * @param      boolean $deepCopy Whether to also copy all rows that refer (by fkey) to the current row.
	 * @throws     PropelException
	 */
	public function copyInto($copyObj, $deepCopy = false)
	{

		$copyObj->setFkAudienceId($this->fk_audience_id);

		$copyObj->setFkScreeningId($this->fk_screening_id);

		$copyObj->setSeatIp($this->seat_ip);

		$copyObj->setSeatStatus($this->seat_status);

		$copyObj->setSeatUniqueId($this->seat_unique_id);

		$copyObj->setSeatCreatedAt($this->seat_created_at);

