		$copyObj->setSeatOsVersion($this->seat_os_version);

		$copyObj->setSeatTimeSpent($this->seat_time_spent);


		$copyObj->setNew(true);

		$copyObj->setSeatId(NULL); // this is a auto-increment column, so set to default value

