	  if (!$callable = sfMixer::getCallable('BaseWtvrMessageQueueGlobal:'.$method))
	  {
	    throw new sfException(sprintf('Call to undefined method BaseWtvrMessageQueueGlobal::%s', $method));
	  }
	
	  array_unshift($arguments, $this);
	
	  return call_user_func_array($callable, $arguments);
	}

} // BaseWtvrMessageQueueGlobal
